import { NativeViewElementNode, registerElement } from 'svelte-native/dom';
import { RadCalendar } from 'nativescript-ui-calendar';

class RadCalendarElement extends NativeViewElementNode {
    constructor() {
        super('radCalendar', RadCalendar);
    }
    navigateForward() {
        this.nativeView.navigateForward();
    }
    navigateBack() {
        this.nativeView.navigateBack();
    }
    goToDate(date) {
        this.nativeView.goToDate(date);
    }
    static register() {
        registerElement('radCalendar', () => new RadCalendarElement());
    }
}

export default RadCalendarElement;
